#Checking status
def isConnected():
    if (web3.isConnected() is True):
        print("Connected sucessfully to PancakeSwap !")
    else:
        print("Lost connection, try again !")

#Get balance of the address
#input: address
#output: wallet balance
def getBalance(address):
    balance = web3.eth.get_balance(address)
    read =  web3.fromWei(balance, 'either')
    print(read)

#Token to buy
#input: wallet address, token contract, token spend (BNB with BSC)
def tokenSwap(address, tokenContract, tokenSpend):
    global pancakeRouter
    global pancakeABI
    token = web3.toChecksumAddress(tokenContract)
    spend = web3.toChecksumAddress(tokenSpend)
    contract = web3.eth.contract(address=pancakeRouter, abi=pancakeABI)
    nonce = web3.eth.get_transaction_count(address)
    start = time.time()
    pancakeswapTxn = contract.functions.swapExactETHForTokens(
        0,
        [spend,token],
        address,
        (int(time.time() + 10000)
        ).buildTransaction({
            'from': address,
            'value': web3.toWei(0.01, 'ether'),
            'gasPrice': web3.toWei(5, 'gwei'),
            'nonce': nonce,
        })
    )
    singed_txn = web3.eth.account.sign_transaction(pancakeswapTxn, PrivateKey = "input 12 private key")
    txtoken = web3.eth.send_raw_transaction(singed_txn.rawTransaction)
    print(web3.toHex(txtoken))


def main():
    isConnected()
    getBalance('walletAddress')
    tokenSwap('walletAddress', 'tokenContract', 'tokenSpend')

if __name__ == "__main__":
    main()